import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import IconPlanify from '../assets/logo.png';
import Create from '../pages/create';

function Dashboard() {
    const [events, setEvents] = useState([]);
    const [editingEvent, setEditingEvent] = useState(null);
    const [loading, setLoading] = useState(true);

    // Fetch data dari db.json yang ada di folder public
    useEffect(() => {
        fetch('http://localhost:5000/events')
          .then((response) => response.json())
          .then((data) => {
            setEvents(data);
            setLoading(false);
          })
          .catch((error) => {
            console.error('Error loading data:', error);
            setLoading(false);
          });
      }, []);

    const handleSaveEvent = (newEvent) => {
        if (editingEvent) {
            // Update event
            fetch(`http://localhost:5000/events/${newEvent.id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newEvent),
            })
            .then((response) => response.json()) // Pastikan kita menerima response JSON
            .then((updatedEvent) => {
                // Update state dengan event yang baru
                setEvents((prev) =>
                    prev.map((event) => (event.id === newEvent.id ? updatedEvent : event))
                );
                setEditingEvent(null);
            })
            .catch((error) => console.error('Error updating event:', error));
        } else {
            // Tambah event baru
            fetch('http://localhost:5000/events', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newEvent),
            })
                .then((response) => response.json())
                .then((createdEvent) => setEvents((prev) => [...prev, createdEvent]))
                .catch((error) => console.error('Error adding event:', error));
        }
    };

    const handleDeleteEvent = (id) => {
        fetch(`http://localhost:5000/events/${id}`, { method: 'DELETE' })
            .then(() => setEvents((prev) => prev.filter((event) => event.id !== id)))
            .catch((error) => console.error('Error deleting event:', error));
    };

    const handleEditEvent = (event) => {
        setEditingEvent(event);
    };

    if (loading) {
        return <p className="text-center">Memuat data...</p>;
    }

    return (
        <div>
            <div className='flex items-center space-x-4 p-4 border-b border-gray-300'>
                <Link to='/beranda'>
                    <img alt="icon-planify" src={IconPlanify} className="w-32" />
                </Link>
                <h1 className='text-2xl font-bold'>Dashboard</h1>
            </div>

            <h1 className="text-2xl font-bold p-4">Dashboard</h1>
            <Create onSave={handleSaveEvent} editingEvent={editingEvent} />
            <div className="mt-8">
                <h2 className="text-xl font-semibold mb-4">Daftar Acara</h2>
                <ul>
                    {events.map((event) => (
                        <li key={event.id} className="border p-4 rounded mb-2">
                            <h3 className="text-lg font-semibold">{event.name}</h3>
                            <p className="text-sm text-gray-600">Waktu: {event.time}</p>
                            <p className="text-sm">{event.description}</p>
                            <div className="space-x-2 mt-2">
                                <button
                                    onClick={() => handleEditEvent(event)}
                                    className="px-2 py-1 bg-yellow-500 text-white rounded">
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDeleteEvent(event.id)}
                                    className="px-2 py-1 bg-red-500 text-white rounded">
                                    Hapus
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
