import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import IconPlanify from '../assets/logo.png';

function Dashboard() {
    const [events, setEvents] = useState([]);
    const [eventName, setEventName] = useState('');
    const [eventTime, setEventTime] = useState('');
    const [eventDescription, setEventDescription] = useState('');
    const [editingEvent, setEditingEvent] = useState(null);

    // Fungsi untuk memuat data dari file JSON
    useEffect(() => {
        fetch('/events.json')
            .then(response => response.json())
            .then(data => setEvents(data))
            .catch(error => console.error('Error loading data:', error));
    }, []);

    // Fungsi untuk menambah acara
    const addEvent = () => {
        if (eventName && eventTime && eventDescription) {
            const newEvent = {
                id: Date.now(), // Menggunakan timestamp sebagai ID unik
                name: eventName,
                time: eventTime,
                description: eventDescription,
            };
            setEvents([...events, newEvent]);
            setEventName('');
            setEventTime('');
            setEventDescription('');
            saveToJSON([...events, newEvent]);
        }
    };

    // Fungsi untuk menyimpan perubahan data ke LocalStorage (simulasi backend)
    const saveToJSON = (updatedEvents) => {
        localStorage.setItem('events', JSON.stringify(updatedEvents));
    };

    // Fungsi untuk menghapus acara
    const deleteEvent = (id) => {
        const updatedEvents = events.filter(event => event.id !== id);
        setEvents(updatedEvents);
        saveToJSON(updatedEvents);
    };

    // Fungsi untuk mulai mengedit acara
    const editEvent = (id) => {
        const eventToEdit = events.find(event => event.id === id);
        setEventName(eventToEdit.name);
        setEventTime(eventToEdit.time);
        setEventDescription(eventToEdit.description);
        setEditingEvent(eventToEdit.id);
    };

    // Fungsi untuk menyimpan perubahan acara yang sedang diedit
    const updateEvent = () => {
        if (eventName && eventTime && eventDescription && editingEvent !== null) {
            const updatedEvents = events.map(event =>
                event.id === editingEvent
                    ? { ...event, name: eventName, time: eventTime, description: eventDescription }
                    : event
            );
            setEvents(updatedEvents);
            setEventName('');
            setEventTime('');
            setEventDescription('');
            setEditingEvent(null);
            saveToJSON(updatedEvents);
        }
    };

    return (
        <div>
            <div className='flex items-center space-x-4 p-4 border-b border-gray-300'>
                <Link to='/beranda'>
                    <img alt="icon-planify" src={IconPlanify} className="w-32" />
                </Link>
                <h1 className='text-2xl font-bold'>Dashboard</h1>
            </div>

            {/* Form input untuk acara */}
            <div className='my-4 p-4'>
                <input
                    type="text"
                    value={eventName}
                    onChange={(e) => setEventName(e.target.value)}
                    className='border border-gray-300 p-2 rounded mb-2 block w-full'
                    placeholder='Nama Acara'
                />
                <input
                    type="text"
                    value={eventTime}
                    onChange={(e) => setEventTime(e.target.value)}
                    className='border border-gray-300 p-2 rounded mb-2 block w-full'
                    placeholder='Waktu Acara (contoh: 10:00 AM)'
                />
                <textarea
                    value={eventDescription}
                    onChange={(e) => setEventDescription(e.target.value)}
                    className='border border-gray-300 p-2 rounded mb-2 block w-full'
                    placeholder='Deskripsi Acara'
                />
                <button
                    onClick={editingEvent ? updateEvent : addEvent}
                    className='px-4 py-2 bg-blue-500 text-white rounded'>
                    {editingEvent ? 'Update Acara' : 'Tambah Acara'}
                </button>
            </div>

            {/* Daftar acara */}
            <div className='w-full max-w-5xl mx-auto mt-4'>
                <ul>
                    {events.map((event) => (
                        <li key={event.id} className='border p-4 rounded mb-2'>
                            <h3 className='text-lg font-semibold'>{event.name}</h3>
                            <p className='text-sm text-gray-600'>Waktu: {event.time}</p>
                            <p className='text-sm'>{event.description}</p>
                            <div className='space-x-2 mt-2'>
                                <button
                                    onClick={() => editEvent(event.id)}
                                    className='px-2 py-1 bg-yellow-500 text-white rounded'>
                                    Edit
                                </button>
                                <button
                                    onClick={() => deleteEvent(event.id)}
                                    className='px-2 py-1 bg-red-500 text-white rounded'>
                                    Hapus
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
