import React, { useState } from 'react';

function Register() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    // Fungsi untuk menangani registrasi
    const handleRegister = (e) => {
        e.preventDefault(); // Mencegah halaman reload saat submit

        const newUser = { email, password };

        fetch('http://localhost:5000/users', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newUser),
        })
            .then((response) => {
                if (response.ok) {
                    setMessage('Registrasi Berhasil!');
                    setEmail('');
                    setPassword('');
                } else {
                    setMessage('Terjadi kesalahan, coba lagi');
                }
            })
            .catch((error) => {
                console.error('Error registering user:', error);
                setMessage('Terjadi kesalahan saat menghubungi server.');
            });
    };

    return (
        <div className="flex justify-center min-h-screen items-center">
            <div className="bg-gray-100 rounded-lg shadow-md p-8 hover:shadow-lg transition w-full max-w-md text-center mb-8">
                <h1 className="text-3xl lg:text-4xl font-bold text-gray-800 mb-8">Register</h1>
                {/* Form registrasi */}
                <form onSubmit={handleRegister}>
                    <div className="mb-4">
                        <label htmlFor="email" className="block text-gray-700 mb-2">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Masukkan email"
                        />
                    </div>

                    <div className="mb-4">
                        <label htmlFor="password" className="block text-gray-700 mb-2">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                            placeholder="Masukkan password"
                        />
                    </div>

                    <button
                        type="submit"
                        className="w-full bg-blue-500 text-white p-2 rounded"
                    >
                        Register
                    </button>
                </form>

                {message && <p className="mt-4 text-gray-700">{message}</p>}
            </div>
        </div>
    );
}

export default Register;
