import React, { useState, useEffect } from 'react';
import Create from './Create';

function Dashboard() {
    const [events, setEvents] = useState([]); // State untuk menyimpan daftar acara
    const [editingEvent, setEditingEvent] = useState(null); // State untuk acara yang sedang diedit

    // useEffect untuk memuat data dari server saat halaman pertama kali dirender
    useEffect(() => {
        fetch('http://localhost:5000/events')
            .then((response) => response.json())
            .then((data) => setEvents(data)) // Menyimpan data ke state
            .catch((error) => console.error('Error loading data:', error));
    }, []);

    const handleSaveEvent = (event) => {
        // Logika untuk menambahkan atau memperbarui acara
        if (editingEvent) {
            // Update event
            const updatedEvents = events.map((e) =>
                e.id === event.id ? event : e
            );
            setEvents(updatedEvents);
        } else {
            // Tambah event baru
            setEvents([...events, event]);
        }
        setEditingEvent(null);
    };

    const handleEditEvent = (id) => {
        const eventToEdit = events.find((event) => event.id === id);
        setEditingEvent(eventToEdit);
    };

    const handleDeleteEvent = (id) => {
        const updatedEvents = events.filter((event) => event.id !== id);
        setEvents(updatedEvents);
    };

    return (
        <div>
            {/* Form Create */}
            <Create onSave={handleSaveEvent} editingEvent={editingEvent} />

            {/* Daftar Event */}
            <div className='mt-8'>
                <h2 className='text-xl font-semibold mb-4'>Daftar Acara</h2>
                <ul>
                    {events.map((event) => (
                        <li key={event.id} className='border p-4 rounded mb-2'>
                            <h3 className='text-lg font-semibold'>{event.name}</h3>
                            <p className='text-sm text-gray-600'>Waktu: {event.time}</p>
                            <p className='text-sm'>{event.description}</p>
                            <div className='space-x-2 mt-2'>
                                <button
                                    onClick={() => handleEditEvent(event.id)}
                                    className='px-2 py-1 bg-yellow-500 text-white rounded'>
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDeleteEvent(event.id)}
                                    className='px-2 py-1 bg-red-500 text-white rounded'>
                                    Hapus
                                </button>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Dashboard;
